list = [1, 2, 3, 4, 5]print(f"Original list: {list}")print(f"First 2 elements: {list[:2]}")print(f"Last 2 elements: {list[-2:]}")print(f"Repetition of list: {list * 2}")list.remove(3)print(f"List after removing 3: {list}")list.insert(2, 3)print(f"List after inserting 3 at index 2: {list}")list.append(6)print(f"List after appending 6: {list}")list.pop()print(f"List after popping the last element: {list}")list.extend([10, 9, 8, 7, 6])print(f"List after extending: {list}")list.reverse()print(f"List after reversing: {list}")list.sort()print(f"List after sorting: {list}")print(f"Index of element 10 is {list.index(10)}")list.append(10)print(f"List after appending another 10: {list}")print(f"Count of element 10 is {list.count(10)}")print(f"Length of the list is {len(list)}")list.clear()print(f"List after clearing: {list}")